function getTitleUsingAjax( value ) 
{ 
  //document.getElementById( 'message' ).innerHTML = 'Uw opdracht wordt verwerkt!';
  jQuery.ajax({
    type     : 'POST',
    url      :use_ajax.ajaxurl,
    data     : ( 
                { 
                  action : 'getTitle',
                  v      : value } ),
    success  : function( data ) {
    window.location = 'http://www.remyvastgoed.com/?s='+data;
   }
  });
}